mapper.select:
SELECT * FROM areas
WHERE ST_Distance_Sphere(
geo_point,
ST_GeomFromText(CONCAT('POINT(', #{lon}, ' ', #{lat}, ')'))
) <= #{distance}

SELECT ST_DISTANCE_SPHERE(
    a1.geo_point,a2.geo_point)<=
    500
    1000
    2000
)
  FROM areas   a1,
       areas   a2
 WHERE a1.id = ${productAreaId}
   AND a2.id = ${areaId}
